import React from 'react'
import { Table } from 'reactstrap';
import { getListStudent, deleteStudent } from '../services'
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
export default function Student() {
    const [listStudent, setListStudent] = React.useState([])

    React.useEffect(() => {
        fetchList()
    }, [])

    const fetchList = () => {
        getListStudent().then(res => {
            setListStudent(res.data.Student)
        })
    }
    const deleteAStudent = (studentId) => {
        deleteStudent(studentId).then(() => fetchList())
    }
    return (
        <div>
            <Label for="nameSearch">Search</Label>
            <Input type="text" name="search" id="nameSearch" placeholder="find name to edit" onChange />
            <Table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Gender</th>
                        <th>University</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {listStudent ? listStudent.map(student =>
                        <tr>
                            <td>{student.id}</td>
                            <td>{student.name}</td>
                            <td>{student.age}</td>
                            <td>{student.gender ? "Male" : "Female"}</td>
                            <td>{student.university}</td>
                            <td>
                                <Button onClick={()=>deleteAStudent(student.id)}>Delete</Button>
                                <Button>Edit</Button>
                            </td>
                        </tr>
                    )
                        : ""}
                </tbody>
            </Table>
        </div>
    )
}
