import React from 'react'
import { getListStudent, deleteStudent } from '../services'
import {Table ,Button, Form, FormGroup, Label, Input, FormText, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

export default function Student() {
    const [listStudent, setListStudent] = React.useState([])
    const [modal, setModal] = React.useState(false);
    const [name, setName] = React.useState('');
    const [age, setAge] = React.useState();
    const [gender, setGender] = React.useState(true);
    const [university, setUniversity] = React.useState();
    const toggle = (student) => {
        setName(student.name)
        setAge(student.age)
        setGender(student.gender)
        setUniversity(student.university)    
        setModal(!modal)

    };
    React.useEffect(() => {
        fetchList()
    }, [])

    const fetchList = () => {
        getListStudent().then(res => {
            setListStudent(res.data.Student)
        })
    }
    const deleteAStudent = (studentId) => {
        deleteStudent(studentId).then(() => fetchList())
    }
    return (
        <div>

            <Table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Gender</th>
                        <th>University</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {listStudent ? listStudent.map(student =>
                        <tr>
                            <td>{student.name}</td>
                            <td>{student.age}</td>
                            <td>{student.gender ? "Male" : "Female"}</td>
                            <td>{student.university}</td>
                            <td>
                                <Button color="danger" style={{"marginRight":"10px"}} onClick={()=>deleteAStudent(student._id)}>Delete</Button>
                                <Button onClick={()=>toggle(student)}>Edit</Button>
                            </td>
                        </tr>
                    )
                        : ""}
                </tbody>
            </Table>
            <div>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
            <Label for="nameBox">Name</Label>
            <Input value={name} type="text" name="name" id="nameBox" />
            <Label for="ageBox">Age</Label>
            <Input value={age} type="text" name="age" id="ageBox" />         
            <Label for="universityBox">University</Label>
            <Input value={university} type="text" name="university" id="universityBox" />
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
        </div>

    )
}
